mapper.select:
SELECT * FROM
reply
WHERE bno = #{bno}	
ORDER BY no DESC;

mapper.insert:
INSERT INTO
reply(bno, replytext, replyer)	
VALUES(#{bno}, #{replytext}, #{replyer});

mapper.update:
UPDATE reply
SET replytext = #{replytext}
WHERE no = #{no};

